package com.syntax.class01;

public class PractiveVariables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//storing whole numbers in variables
		byte variable = 55;
		short variable2 = 3423;
		int variable3 = 435453143;
		long var4 = 98569828L;
		
		
		//storing decimal numbers in variables
		float myFloat = 4.99f;
		double myDouble = 6.837893274;
				
		//storing single character
		char singleLetter='K';
		char specialCharacter='@';
		
		//storing boolean values
		boolean myBoo1= false;
		boolean myBoo2= true;
		
		System.out.println(myBoo1);
		
		
	}

}
