package com.syntax.class02;

public class ArithmeticOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/* 
		 * +, -, *, %, modulus are arithmetic operators
		 */
		
		int num1=90;
		int num2=10;
		System.out.println(num1+num2);
		
		//type syso then hit +control+space
		
		System.out.println(num1+num2);
		System.out.println(num1-num2);
		System.out.println(num1*num2);
		System.out.println(num1/num2);
		System.out.println(num1%num2);
		System.out.println("line 6");
		
		int sum=(num1+num2);
		System.out.println(sum);
		
		float f1=2.99f;
		float f2=3.99f;
		double n1=2.99;
		double n2=3.99;
		
		float quotient=f1/f2;
		System.out.println(quotient);
		System.out.println(n1/n2);
		
		//modulus % are remainders
		
		int number1, number2, mod;
		
		number1=10;
		number2=2;
		mod=number1%number2;
		System.out.println(mod);
		
		//what is the result?
		int guess; //declared a variable
		guess=20+20*2; //assign value to variable guess
		System.out.println(guess); //assigned values
		
		guess=2*2+(4*10); //REassigned the value
		System.out.println(guess); //reassigned values
		
		
		
		
		
		
		
		
		
		
	}

}
