package com.syntax.review5;

public class NestedLoop3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*
		 * 12345 printed four times
		 */
		
		for(int j=0; j<4; j++) {
		for(int i=1; i<6; i++) {
			System.out.print(i);
		}
		} System.out.println( );
	
		//tip for patterns: start with inner loop and analyze logic
	}

}
